gameforge.locale.monster_chat_11000_8 = "Un Ninja experimentat va stapani toate tainele razboiului in lupta cu sabia!"
gameforge.locale.monster_chat_11000_9 = "Daca vrei, ca razboinic al zeului zmeu, sa stapanesti luptele, alege invatatura puterii zmeului!"
