gameforge.locale.monster_chat_11002_25 = "Termina misiunea si foloseste recompensa pentru upgrade!"
gameforge.locale.monster_chat_11002_26 = "Pescuit? Suna bine! Ai deja undita si momeala de la un pescar, nu?"
gameforge.locale.monster_chat_11002_27 = "Vrei sa gasesti ceva mai puternic de vanat? Cauta un portal! Mergi intr-un loc de vanatoare care corespunde nivelului tau!"
gameforge.locale.monster_chat_11002_28 = "Pazeste-te de inamici din alte tari. Nu stii niciodata cand te ataca pe la spate!"
gameforge.locale.monster_chat_11002_29 = "Vrei sa iti innoiesti abilitatile? Mergi la femeia batrana! Vezi sa ai destul Yang la tine."
gameforge.locale.monster_chat_11002_3 = "Invatatorul luptei cu sabia te va introduce in lumea celor mai puternici razboinici!"
gameforge.locale.monster_chat_11002_30 = "Aspira la nivelul de maestru perfect. Doar asa vei ajunge mare!"
gameforge.locale.monster_chat_11002_31 = "Daca vrei sa intri intr-un alt oras sau camp de lupta, cauta oameni batrani pe continent!"
gameforge.locale.monster_chat_11002_4 = "Daca vrei sa dobandesti abilitatile razboinicului, mergi si invata de la maestrul in arta razboiului!"
gameforge.locale.monster_chat_11002_5 = "Daca vrei sa devii un Sura maestru in folosirea armelor, atunci alege Invatatura Arme Magice!"
gameforge.locale.monster_chat_11002_6 = "Inscrie-te la invatatura magiei negre, daca vrei sa stapanesti vjajile intunecate!"
gameforge.locale.monster_chat_11002_7 = "Lupta cu Arcul este doar pentru Ninja ce au trecut de levelul 5."
gameforge.locale.monster_chat_11002_8 = "Un Ninja experimentat va stapani toate tainele razboiului in lupta cu sabia!"
gameforge.locale.monster_chat_11002_9 = "Daca vrei, ca razboinic al zeului zmeu, sa stapanesti luptele, alege invatatura puterii zmeului!"
gameforge.locale.monster_chat_11004_1 = "Ascultati, toti cei care va faceti griji pentru sanatatea voastra!"
gameforge.locale.monster_chat_11004_10 = "Daca vrei sa stapanesti puterea naturii, mai ales fulgerele, iti recomand invatatura vindecarii!"
gameforge.locale.monster_chat_11004_11 = "Deosebit la razboinic, este felul in care abordeaza luptele. Concentreaza-te asupra antrenamentului corpului tau!"
gameforge.locale.monster_chat_11004_12 = "Puterea Sura este magia. Lucreaza fara pauza la inteligenta, viteza si puterea corpului tau!"
gameforge.locale.monster_chat_11004_13 = "Du-ti abilitatile ninja la un nivel superior! Concentreaza-te asupra vitezei si a puterii corporale!"
gameforge.locale.monster_chat_11004_14 = "samanii folosesc in timpul bataliilor regenerarea si vrajile de sustinere sau atac. Inteligenta este vitala pentru ei!"
gameforge.locale.monster_chat_11004_15 = "Nu uita invatatura!"
gameforge.locale.monster_chat_11004_16 = "Adera la o invatatura pentru a-ti exersa abilitatile!"
gameforge.locale.monster_chat_11004_17 = "Abilitati? Poti atinge nivelul de maestru, impartit aleatoriu intre nivelul 17 si 21!"
gameforge.locale.monster_chat_11004_18 = "Doresti sa devi maestru profesionist? Atunci ai nevoie de o carte speciala a skillurilor!"
gameforge.locale.monster_chat_11004_19 = "De indata ce termini de citit o carte a abilitatilor, aceasta dispare!"
gameforge.locale.monster_chat_11004_2 = "Toti razboinicii incepand cu nivelul 5 sa fie atenti!"
gameforge.locale.monster_chat_11004_20 = "Doresti sa citesti inca o carte a abilitatilor? Trebuie sa aatepti o zi."
gameforge.locale.monster_chat_11004_21 = "Imbunatateste-ti inteligenta pentru a putea imbunatati abilitatile de Sura!"
gameforge.locale.monster_chat_11004_22 = "Traieste marea putere Sura in lupta!"
gameforge.locale.monster_chat_11004_23 = "stii unde este templul sectei esoterice? Se afla in mijlocul Vaii Seungryong!"
gameforge.locale.monster_chat_11004_24 = "Au aparut dinnou pesterile paianjenilor. Se afla in desertul Yongbi!"
gameforge.locale.monster_chat_11004_25 = "Termina misiunea si foloseste recompensa pentru upgrade!"
gameforge.locale.monster_chat_11004_26 = "Pescuit? Suna bine! Ai deja undita si momeala de la un pescar, nu?"
gameforge.locale.monster_chat_11004_27 = "Vrei sa gasesti ceva mai puternic de vanat? Cauta un portal! Mergi intr-un loc de vanatoare care corespunde nivelului tau!"
gameforge.locale.monster_chat_11004_28 = "Pazeste-te de inamici din alte tari. Nu stii niciodata cand te ataca pe la spate!"
gameforge.locale.monster_chat_11004_29 = "Vrei sa iti innoiesti abilitatile? Mergi la femeia batrana! Vezi sa ai destul Yang la tine."
gameforge.locale.monster_chat_11004_3 = "Invatatorul luptei cu sabia te va introduce in lumea celor mai puternici razboinici!"
gameforge.locale.monster_chat_11004_30 = "Aspira la nivelul de maestru perfect. Doar asa vei ajunge mare!"
gameforge.locale.monster_chat_11004_31 = "Daca vrei sa intri intr-un alt oras sau camp de lupta, cauta oameni batrani pe continent!"
gameforge.locale.monster_chat_11004_4 = "Daca vrei sa dobandesti abilitatile razboinicului, mergi si invata de la maestrul in arta razboiului!"
gameforge.locale.monster_chat_11004_5 = "Daca vrei sa devii un Sura maestru in folosirea armelor, atunci alege Invatatura Arme Magice!"
gameforge.locale.monster_chat_11004_6 = "Inscrie-te la invatatura magiei negre, daca vrei sa stapanesti vjajile intunecate!"
gameforge.locale.monster_chat_11004_7 = "Lupta cu Arcul este doar pentru Ninja ce au trecut de levelul 5."
gameforge.locale.monster_chat_11004_8 = "Ninja poate fi un adversar extrem de periculos atunci cand invata lupta cu sabia!"
gameforge.locale.monster_chat_11004_9 = "Daca vrei sa ai control asupra luptelor ca si un razboinic al Zeului Dragon, alege Puterea Dragonului!"
gameforge.locale.monster_chat_1105_1 = "Starea de inghetare este buna."
gameforge.locale.monster_chat_1105_2 = "Muntele Sohan este cu siguranta cel mai bun."
gameforge.locale.monster_chat_1105_3 = "Ce vreme frumoasa."
gameforge.locale.monster_chat_1105_4 = "Am sa te fac identic cu mine."
gameforge.locale.monster_chat_1105_5 = "Poti trai la nesfarsit, inainte ca gheata sa se topeasca."
gameforge.locale.monster_chat_1105_6 = "Cum crezi ca o sa te simti atunci cand am sa te transform incet intr-o bucata de gheata?"
gameforge.locale.monster_chat_1106_1 = "Oameni...aici?!"
gameforge.locale.monster_chat_1106_2 = "Obosit...dar destul de treaz pentru tine..."
gameforge.locale.monster_chat_1106_3 = "Cineva pe meleagurile mele? "
gameforge.locale.monster_chat_1106_4 = "Nu aparti acestui loc!"
gameforge.locale.monster_chat_1106_5 = "Mai bine te-ai retrage!"
gameforge.locale.monster_chat_1106_6 = "Ciudat...atat de singur?"
gameforge.locale.monster_chat_1191_1 = "A trecut ceva timp de cand nu am mai vazut un luptator experimentat pe aici."
gameforge.locale.monster_chat_1191_2 = "Nimic altceva de facut decat sa-ti ascuti armele..."
gameforge.locale.monster_chat_1191_3 = "Oare ar trebui sa lucrez la ele?"
gameforge.locale.monster_chat_1191_4 = {}
gameforge.locale.monster_chat_1191_5 = "Ceva lipseste. Nu sunt multumit."
gameforge.locale.monster_chat_1191_6 = "Am sa pun repede capat suferintei tale. Acesta ar fi ultimul."
gameforge.locale.monster_chat_1301_1 = "Gat alungit...!"
gameforge.locale.monster_chat_1301_2 = "Culoare pielii iti va palii si mai mult intr-un minut."
gameforge.locale.monster_chat_1301_3 = "Vrei sa ma provoci? OK!"
gameforge.locale.monster_chat_1301_4 = "M-ai atacat! Pweck! Nu poti supravietui."
gameforge.locale.monster_chat_1301_5 = "Oameni slabi, slabi!"
gameforge.locale.monster_chat_1301_6 = "Sa vedem cat poti rezista!"
gameforge.locale.monster_chat_1302_1 = "Vrei sa scapi? Nici sa nu te gandesti."
gameforge.locale.monster_chat_1302_2 = "Asa. Vino mai aproape. Pwiehk!"
gameforge.locale.monster_chat_1302_3 = "Sabia tremura in mana mea. Pwiehk!"
gameforge.locale.monster_chat_1302_4 = "Nu vei supravietui acestei incercari!"
gameforge.locale.monster_chat_1302_5 = "Aici vine sabia mea!"
gameforge.locale.monster_chat_1302_6 = "Dureros, nu? (Ras)"
gameforge.locale.monster_chat_1303_1 = "Chiar si iarba calcata din greseala poate ucide."
gameforge.locale.monster_chat_1303_2 = "Atentie la iarba."
gameforge.locale.monster_chat_1303_3 = "Asa. Vino frumos la mine. (Ras)"
gameforge.locale.monster_chat_1303_4 = "Indraznesti sa te apropii de mine? Bine... atunci trebuie sa suporti consecintele!"
gameforge.locale.monster_chat_1303_5 = "Mori! Mori!"
gameforge.locale.monster_chat_1303_6 = "Regreti ca ai venit?"
gameforge.locale.monster_chat_1305_1 = "Tu? Vrei sa lupti?"
gameforge.locale.monster_chat_1305_2 = "Tu? Vrei sa lupti?"
gameforge.locale.monster_chat_1305_3 = "Vino daca vrei!"
gameforge.locale.monster_chat_1305_4 = "Te gandeai sa fugi, nu-i asa?"
gameforge.locale.monster_chat_1305_5 = "Ai subestimat valoarea sabiei mele."
gameforge.locale.monster_chat_1305_6 = "Þi-e frica? (Ras)"
gameforge.locale.monster_chat_1901_1 = "Cred ca o sa mori in mainile mele azi."
gameforge.locale.monster_chat_1901_2 = "Oh, imi place de tine."
gameforge.locale.monster_chat_1901_3 = "Cum arata? Schimbarea mea draga."
gameforge.locale.monster_chat_1901_4 = "Hei! Sa fugi ca un las nu te va ajuta cu nimic."
gameforge.locale.monster_chat_1901_5 = "Aspectul meu ti-a provocat fiori pe sira spinarii?"
gameforge.locale.monster_chat_1901_6 = "Nu vrei sa mai strigi ceva? (Ras)"
gameforge.locale.monster_chat_20001_1 = "Pari nervos... Nu te ingrijora, nu va exploda nimic."
gameforge.locale.monster_chat_20001_2 = "Din cand in cand as vrea sa merg la plimbare."
gameforge.locale.monster_chat_20001_3 = "E asa de plictisitor. Nici macare nepotii mei nu ma mai viziteaza."
gameforge.locale.monster_chat_20001_4 = "Cand esti batran ai nevoie de companie."
gameforge.locale.monster_chat_20001_5 = "Zilele mele de glorie au apus."
gameforge.locale.monster_chat_20002_1 = "Soarele este asa de cald."
gameforge.locale.monster_chat_20002_2 = "Minunata vreme pentru a usca o panza uda, nu-i asa?"
gameforge.locale.monster_chat_20002_3 = "Unde mi-o fi neispravitul de barbat?"
gameforge.locale.monster_chat_20002_4 = "Isi spune tata, si nici macar nu se rusineaza in fata copiilor din cauza marionetei sale!"
gameforge.locale.monster_chat_20002_5 = "Cartile sunt carti. Lucreaza si nu castiga nimic. Citeste doar!"
gameforge.locale.monster_chat_20003_1 = "Oh, dududu, draga. Ai dormit bine?"
gameforge.locale.monster_chat_20003_2 = "Zambesti ca un ingeras."
gameforge.locale.monster_chat_20003_3 = "Mama ta se bucura ca esti asa sanatos."
gameforge.locale.monster_chat_20003_4 = "Iara vrei atentie? Þi-e foame?"
gameforge.locale.monster_chat_20003_5 = "Copil cuminte, copilul nostru. Dormi, copilule, dormi."
gameforge.locale.monster_chat_20004_1 = "Pur si simplu o noua zi."
gameforge.locale.monster_chat_20004_2 = "Unde pot manca astazi ceva?"
gameforge.locale.monster_chat_20004_3 = "Intotdeauna este la fel cu spadasinii astia."
gameforge.locale.monster_chat_20004_4 = "Ia sa vedem...Poate ar trebui sa merg la Octavio la restaurant."
gameforge.locale.monster_chat_20004_5 = "Ah da, se spune ca nu va mai fi razboi..."
gameforge.locale.monster_chat_20005_1 = "Sufletul unui maestru in olarit se afla in varful degetelor sale."
gameforge.locale.monster_chat_20005_2 = "De cand tara a fost cuprinsa de haos, vezi peste tot numai luptatori."
gameforge.locale.monster_chat_20005_3 = "Toti vor sa se lutpe mereu."
gameforge.locale.monster_chat_20005_4 = "Eu ma rog pentru o lume linistita si impacata."
gameforge.locale.monster_chat_20005_5 = "Hei, tu acolo! Ia-ti macar putin timp sa te relaxezi si sa cugeti."
gameforge.locale.monster_chat_20006_1 = "Ahhh, ce fac oare?"
gameforge.locale.monster_chat_20006_2 = "Nu dau de urma fratelui meu. Zicea ca vrea sa vina."
gameforge.locale.monster_chat_20006_3 = "Iar acum nu vad decat acesti luptatori cu sabiile lor."
gameforge.locale.monster_chat_20006_4 = "Mi-e dor de fratele meu."
gameforge.locale.monster_chat_20006_5 = "L-a vazut cineva pe fratele meu?"
gameforge.locale.monster_chat_20007_1 = "Mmmmmh, carnea aceasta este proaspata!"
gameforge.locale.monster_chat_20007_2 = "Ce bine ca oamenii mananca din nou mai multa carne."
gameforge.locale.monster_chat_20007_3 = "Afacerea infloreste, dar carnea e cam putina."
gameforge.locale.monster_chat_20007_4 = "Cand ajunge carnea pe care am comandat-o?"
gameforge.locale.monster_chat_20007_5 = "Daca nu vine la timp trebuie sa mergem la vanatoare."
gameforge.locale.monster_chat_20008_1 = "Sper ca pe asa o vreme, clientii ne vor navali in casa."
gameforge.locale.monster_chat_20008_2 = "Oh! Cainele lenes va veni sigur in curand si va vrea sa manance ceva."
gameforge.locale.monster_chat_20008_3 = "Na, ce amarat!"
gameforge.locale.monster_chat_20008_4 = "Mai demult era un razboinic foarte bun."
gameforge.locale.monster_chat_20008_5 = "Ma intreb, de ce nu mai lupta?"
gameforge.locale.monster_chat_20009_1 = "Tinerii astia...atat de plini de viata si imaturi!"
gameforge.locale.monster_chat_20009_2 = "Luptatorii arata mult mai bine cand poarta arme."
gameforge.locale.monster_chat_20009_3 = "Acest spirit criminal implineste pe fiecare."
gameforge.locale.monster_chat_20009_4 = "Asculta-ti inima, concentreaza-te si elibereaza-ti sufletul."
gameforge.locale.monster_chat_20009_5 = "Nu exista reteta pentru fericire insa, iti pot da cateva sfaturi pe cate te poti axa."
gameforge.locale.monster_chat_20010_1 = "Am adus un obiect bun!"
gameforge.locale.monster_chat_20010_2 = "Bunuri din tinuturi indepartate! Nemaipomenit, lucruri pretioase!"
gameforge.locale.monster_chat_20010_3 = "Veniti incoace, veniti cu totii incoace!"
gameforge.locale.monster_chat_20010_4 = "Cumparati, cumparati atata timp cat mai este. Asa ceva nu se gaseste peste tot si oricand!"
gameforge.locale.monster_chat_20010_5 = "Sunt prea multi talhari pe aici. Ar trebui sa stea departe de bunurile mele!"
gameforge.locale.monster_chat_20011_1 = "Acum este foarte uscat aici."
gameforge.locale.monster_chat_20011_2 = "Recolta de grane nu este asa de buna ca mai demult."
gameforge.locale.monster_chat_20011_3 = "Ce impiedica dezvoltarea pe campii?"
gameforge.locale.monster_chat_20011_4 = "Cerealele sunt sfaramicioase si au gust de putred."
gameforge.locale.monster_chat_20011_5 = "Semn rau. Agricultura este baza tuturor lucrurilor."
gameforge.locale.monster_chat_20012_1 = "Placinta cu orez! Delicioasa placinta cu orez!"
gameforge.locale.monster_chat_20012_2 = "Vand prajituri de orez dulce!"
gameforge.locale.monster_chat_20012_3 = "Oh! Afacerea merge prost."
gameforge.locale.monster_chat_20012_4 = "Ar trebui sa incerc in alt oras."
gameforge.locale.monster_chat_20012_5 = "Dar exista asa multe animale salbatice de prada pe aici..."
gameforge.locale.monster_chat_20013_1 = "Intr-o astfel de zi, cel mai bine este in barca."
gameforge.locale.monster_chat_20013_2 = "Vreme perfecta de pescuit."
gameforge.locale.monster_chat_20013_3 = "Acum un al nod...Perfect! Nici un peste nu o sa scape din acest carlig!"
gameforge.locale.monster_chat_20013_4 = "Acest urs blestemat mi-a distrus undita."
gameforge.locale.monster_chat_20013_5 = "Ar trebui sa repar din nou plasele."
gameforge.locale.monster_chat_20014_1 = "Hu!"
gameforge.locale.monster_chat_20014_2 = "Nu iti face griji din cauza mea. Asa sunt eu."
gameforge.locale.monster_chat_20014_3 = "Haaaaa!"
gameforge.locale.monster_chat_20014_4 = "Ce ar trebui sa fac ca sa... Offf, ce am mai spus acum?"
gameforge.locale.monster_chat_20014_5 = "Nu pot fi curajos. Hu!"
gameforge.locale.monster_chat_20015_1 = "De 20 de ani aceasta secure imi serveste cu incredere."
gameforge.locale.monster_chat_20015_2 = "Este inca ascutita, dar nu asa ca mai demult."
gameforge.locale.monster_chat_20015_3 = "As vrea sa o intaresc, prin folosirea ei si prin faptul ca lovesc cu ea."
gameforge.locale.monster_chat_20015_4 = "Dar pentru asta am nevoie de niste pietre bogate in minerale."
gameforge.locale.monster_chat_20015_5 = "Dar cum sa mi le procur? "
gameforge.locale.monster_chat_20016_1 = "Clientii imi navalesc pe usa."
gameforge.locale.monster_chat_20016_2 = "Sper sa termin totul la timp."
gameforge.locale.monster_chat_20016_3 = "Lovitura dupa lovitura si nicio plangere..."
gameforge.locale.monster_chat_20016_4 = "Asa trebuie sa sune lovitura de ciocan!"
gameforge.locale.monster_chat_20016_5 = "Cam cald aici."
gameforge.locale.monster_chat_20017_1 = "Nu imi vine sa cant la un instrument."
gameforge.locale.monster_chat_20017_2 = "Instrumentul meu se afla intr-o stare ingrozitoare."
gameforge.locale.monster_chat_20017_3 = "Poate ca ar trebui sa-mi cumpar un instrument nou?"
gameforge.locale.monster_chat_20017_4 = "Instrumentul meu este vechi si uzat... iubesc lucrurile vechi!"
gameforge.locale.monster_chat_20017_5 = "Imi trebuie urgent ceva sa inlocuiesc corzile."
gameforge.locale.monster_chat_20018_1 = "Sunt asa de ocupat!"
gameforge.locale.monster_chat_20018_2 = "In fiecare zi vin alti raniti."
gameforge.locale.monster_chat_20018_3 = "Nu avem timp de distractie!"
gameforge.locale.monster_chat_20018_4 = "Iar medicamentele se termina."
gameforge.locale.monster_chat_20018_5 = "Ar fi bine daca cineva ar putea procura ingredientele pentru medicamente."
gameforge.locale.monster_chat_20019_1 = "De 30 de ani strabat muntii cu arcul."
gameforge.locale.monster_chat_20019_2 = "Astfel mi-am hranit sotia si am platit pentru educatia celor 3 copii."
gameforge.locale.monster_chat_20019_3 = "A fost greu, dar a meritat."
gameforge.locale.monster_chat_20019_4 = "Dar acum s-a rupt coarda arcului. Inseamna ca acum sunt si eu batran?"
gameforge.locale.monster_chat_20019_5 = "Daca nu-mi pot repara arcul trebuie sa cumpar unul nou, dar nu am suficienti bani pentru asta."
gameforge.locale.monster_chat_20020_1 = "Hei! La ce te uiti?"
gameforge.locale.monster_chat_20020_2 = "Nu te apropia!"
gameforge.locale.monster_chat_20020_3 = "Oh, cat va trebui sa ma mai ascund in acest hal?"
gameforge.locale.monster_chat_20020_4 = "Voi muri uitat intr-un colt?"
gameforge.locale.monster_chat_20020_5 = "Ahhhhh, sper sa ma ajute cineva!"
gameforge.locale.monster_chat_20021_1 = "Aceasta zi se va sfarsi ca oricare alta."
gameforge.locale.monster_chat_20021_2 = "Ce pot sa fac? Hm?"
gameforge.locale.monster_chat_20021_3 = "Nu am crezut niciodata ca voi sfarsi asa."
gameforge.locale.monster_chat_20021_4 = "Nu-mi vine sa cred ca el nu mai este aici."
gameforge.locale.monster_chat_20021_5 = "El nu merita asta."
gameforge.locale.monster_chat_20022_1 = "si dinnou o zi buna."
gameforge.locale.monster_chat_20022_2 = "Sotul lui Ariyoung a disparut."
gameforge.locale.monster_chat_20022_3 = "Am vrut sa ajut, dar aceasta incapatanare..."
gameforge.locale.monster_chat_20022_4 = "Daca s-ar fi casatorit cu mine, ar fi putut renunta la viata ei plictisitoare."
gameforge.locale.monster_chat_20022_5 = "Poate ar fi trebuit sa ii arat mai bine sentimentele mele."
gameforge.locale.monster_chat_20023_1 = "Oh nu! Ce ma fac daca afla sotia mea?"
gameforge.locale.monster_chat_20023_2 = "Daca as putea trai din cititul cartilor!"
gameforge.locale.monster_chat_20023_3 = "Cartile sunt atat de misterioase!"
gameforge.locale.monster_chat_20023_4 = "Nu pot trai asa la nesfarsit, dar..."
gameforge.locale.monster_chat_20023_5 = "Sper ca pot face ceva pentru sotia mea, dar..."
gameforge.locale.monster_chat_20024_1 = "Haide, pune mai mult efort!"
gameforge.locale.monster_chat_20024_2 = "Ce faci acolo, baiete? Nu merge mai repede?"
gameforge.locale.monster_chat_20024_3 = "Doar imagineaza-ti un razboinic manuind o sabie si fa la fel!"
gameforge.locale.monster_chat_20024_4 = "Unde e prostul asta? Unde e?"
gameforge.locale.monster_chat_20024_5 = "Acest fiu de catea! Tu nu esti barbat. Ha!"
gameforge.locale.monster_chat_2091_1 = "Taie, taie... parul, gatul..."
gameforge.locale.monster_chat_2091_2 = "Am ceva pentru tine: somnul de veci!"
gameforge.locale.monster_chat_2091_3 = "Trage aer in piept. Nu te ingrijora, este doar otrava!"
gameforge.locale.monster_chat_2091_4 = "Iti voi lasa doar fata! Vei arata ca un ornament dragut..."
gameforge.locale.monster_chat_2091_5 = {}
gameforge.locale.monster_chat_2091_6 = "Infasurat in matase vei arata ca un aperitiv elegant mai tarziu..."
gameforge.locale.monster_chat_2092_1 = "Nu au mai ramas multi dintre noi."
gameforge.locale.monster_chat_2092_2 = "Ei nu sunt creaturile cu doua picioare care se presupune ca ar conduce aici."
gameforge.locale.monster_chat_2092_3 = "Oh!? Se pare ca s-a prins cineva in panza de paianjen."
gameforge.locale.monster_chat_2092_4 = "Sunt o persoana pasnica. Doar ca nu-mi place sa fiu iritat."
gameforge.locale.monster_chat_2092_5 = "Asta este tot ce poti face?"
gameforge.locale.monster_chat_2092_6 = "Nimeni nu se poate pune cu mine!"
gameforge.locale.monster_chat_2104_1 = "De ce oamenii sunt asa aposi pe dinauntru? Imi rugineste sabia!"
gameforge.locale.monster_chat_2104_2 = "Inca nu si-a facut efectul otrava?"
gameforge.locale.monster_chat_2104_3 = "Cum sta treaba cu luptatul?"
gameforge.locale.monster_chat_2104_4 = "Deja obosit de la arsita? "
gameforge.locale.monster_chat_2104_5 = "Las cu placere pe cineva in urma in desert."
gameforge.locale.monster_chat_2104_6 = "Saracul, pur si simplu ingropat in desert."
gameforge.locale.monster_chat_2105_1 = "Iti spun un secret. Urasc mizeria."
gameforge.locale.monster_chat_2105_2 = "Este foarte cald, nu?"
gameforge.locale.monster_chat_2105_3 = "Acest loc este periculos."
gameforge.locale.monster_chat_2105_4 = {}
gameforge.locale.monster_chat_2105_5 = "Te nimeresc intre sprancene."
gameforge.locale.monster_chat_2106_1 = "Se pare ca acolo este cineva pe care am sa-l pun la pamant."
gameforge.locale.monster_chat_2106_2 = "Oh da, imi vad urmatoarea osanda."
gameforge.locale.monster_chat_2106_3 = "(Ras) Imi place, imi place foarte mult!"
gameforge.locale.monster_chat_2106_4 = "Acela de acolo gras si frumos. E al meu!"
gameforge.locale.monster_chat_2106_5 = {}
gameforge.locale.monster_chat_2106_6 = "Iubesc mirosul de sange."
gameforge.locale.monster_chat_2107_1 = "(Vantura) In ziua de azi e putin de lucru. (Vantura)"
gameforge.locale.monster_chat_2107_2 = "Hei! Sunt chiar grase!"
gameforge.locale.monster_chat_2107_3 = "Coducatorul va fi incantat..."
gameforge.locale.monster_chat_2107_4 = {}
gameforge.locale.monster_chat_2107_5 = "Sa mai vanturam odata piciorul!"
gameforge.locale.monster_chat_2107_6 = "Aceasta sageata este numai pentru tine."
gameforge.locale.monster_chat_2108_1 = "Daca il nimeresc pe vreunul, atunci... "
gameforge.locale.monster_chat_2108_2 = "Hei, de ce te uiti?"
gameforge.locale.monster_chat_2108_3 = "Crezi ca sunt usor de invins? Hm?"
gameforge.locale.monster_chat_2108_4 = "Asa un ghinion, sa ma prinda aici in desert!"
gameforge.locale.monster_chat_2108_5 = "Ah, sentimentul asta!! O sa ma bucur de el la nesfarsit!"
gameforge.locale.monster_chat_2108_6 = "Multumesc!"
gameforge.locale.monster_chat_2191_1 = "Simt miros de oameni. Ce miros delicios!"
gameforge.locale.monster_chat_2191_2 = "Bine. Veniti la mine."
gameforge.locale.monster_chat_2191_3 = {}
gameforge.locale.monster_chat_2191_4 = "Ce ma bucur ca te-ai ratacit si ai ajuns pe aici!"
gameforge.locale.monster_chat_2191_5 = "Ramai pe loc! Kkkkkkk!"
gameforge.locale.monster_chat_2191_6 = "Muscatura cu muscatura o sa te las fara carne..."
gameforge.locale.monster_chat_2202_1 = "Apropie-te ca sa-ti smulg pielea."
gameforge.locale.monster_chat_2202_2 = "Stii cate dureri ti-ar putea provoca o temperatura atat de ridicata?"
gameforge.locale.monster_chat_2202_3 = "Poate ar trebui sa-mi sting arsurile cu niste sange proaspat."
gameforge.locale.monster_chat_2202_4 = "Sangele tau...lasa-ma sa-l gust."
gameforge.locale.monster_chat_2202_5 = "Nu exista niciun motiv pentru a incepe o lupta."
gameforge.locale.monster_chat_2202_6 = "Poti deveni ca mine! Numai flacari si groaza."
gameforge.locale.monster_chat_2204_1 = "Atata timp cat sunt eu aici, nu intra nimeni!"
gameforge.locale.monster_chat_2204_2 = "Dintr-un oarecare motiv imi devii o povara."
gameforge.locale.monster_chat_2204_3 = {}
gameforge.locale.monster_chat_2204_4 = "Kwuuh, stii unde esti?"
gameforge.locale.monster_chat_2204_5 = {}
gameforge.locale.monster_chat_2204_6 = "Esti cel mai patetic oponent al meu!"
gameforge.locale.monster_chat_2205_1 = "O sabie isi indeplineste rolul doar in mana unui razboinic."
gameforge.locale.monster_chat_2205_2 = "Þine sabia tot timpul in mana."
gameforge.locale.monster_chat_2205_3 = "Razboiul bate la usa."
gameforge.locale.monster_chat_2205_4 = "si acum luptam pana la moarte!"
gameforge.locale.monster_chat_2205_5 = "Vrei sa rezisti mai departe atacurilor?"
gameforge.locale.monster_chat_2205_6 = "Da, nu ar trebui sa mori asa repede."
gameforge.locale.monster_chat_2206_1 = "Lava este elixirul vietii."
gameforge.locale.monster_chat_2206_2 = "Lava ma tine in viata iar pe tine te va ucide!"
gameforge.locale.monster_chat_2206_3 = "Nu este minunata aceasta lume care clocoteste in lava?"
gameforge.locale.monster_chat_2206_4 = "Indraznesti sa ma deranjezi?"
gameforge.locale.monster_chat_2206_5 = "Ar trebui sa pleci daca iti pasa de viata ta."
gameforge.locale.monster_chat_2206_6 = "Vei regreta prezenta mea aici."
gameforge.locale.monster_chat_2291_1 = "Este o zi frumoasa. Tot ce as vrea ar fi sa am putina liniste."
gameforge.locale.monster_chat_2291_2 = "Nimic nu se poate masura cu tara lui Doyyumhwan."
gameforge.locale.monster_chat_2291_3 = "Putin sange proaspat ar fi o schimbare placuta."
gameforge.locale.monster_chat_2291_4 = "Se pare ca-ti cauti moartea, altfel nu m-ai fi deranjat. "
gameforge.locale.monster_chat_2291_5 = "Deja regreti ca ai ajuns aici?"
gameforge.locale.monster_chat_2291_6 = "Prea tarziu sa mai fugi. Infrunta-ti temerile."
gameforge.locale.monster_chat_301_1 = "Aceste vremuri tensionate ma fac sa-mi pierd rabdarea."
gameforge.locale.monster_chat_301_2 = "Acum doua zile o fata tanara a fost mutilata de animalele din aceasta zona."
gameforge.locale.monster_chat_301_3 = "Animalele salbatice devin din ce in ce mai agresive. Trebuie sa omoram cat de multe putem."
gameforge.locale.monster_chat_301_4 = "Azi nu sunt intr-o dispozitie buna. Strainii ar trebui sa stea departe de mine."
gameforge.locale.monster_chat_301_5 = "Ultimul razboinic pe care l-am omorat ma implora pentru viata lui."
gameforge.locale.monster_chat_302_1 = "Cineva trebuie sa faca ceva cu aceste pietre."
gameforge.locale.monster_chat_302_2 = "Nu am incredere in dimineata asta linistita."
gameforge.locale.monster_chat_302_3 = "Presiunea atmosferica imi da o stare de inconfort."
gameforge.locale.monster_chat_302_4 = "Aceste sageti au fost ascutite foarte bine."
gameforge.locale.monster_chat_302_5 = "Daca as fi in locul tau as avea grija. Sageata se indreapta chiar spre inima ta."
gameforge.locale.monster_chat_302_6 = "Ar trebui sa merg acolo si sa ucid cateva dintre acele bestii."
gameforge.locale.monster_chat_303_1 = "Invatatura artelor martiale este esentiala in aceste vremuri."
gameforge.locale.monster_chat_303_2 = "Ce se intampla daca deodata dau buzna soldati ai guvernului?"
gameforge.locale.monster_chat_303_3 = "De ce te uiti asa la mine? "
gameforge.locale.monster_chat_303_4 = "Lasii au o sansa mica de supravietuire."
gameforge.locale.monster_chat_303_5 = "Nu te supraestima."
gameforge.locale.monster_chat_303_6 = "Se pare ca cineva te-a antrenat foarte bine."
gameforge.locale.monster_chat_304_1 = "Chiar si stramosii mei au reusit sa se afirme in lupta pe aceste teritorii."
gameforge.locale.monster_chat_304_2 = "Trebuie sa stai in alerta si bine pregatit pe aici."
gameforge.locale.monster_chat_304_3 = "Nici sa nu te gandesti sa ma faci sa-mi pierd timpul."
gameforge.locale.monster_chat_304_4 = "Deci, cum te simti cand viata ta este in pericol?"
gameforge.locale.monster_chat_304_5 = "Oh nu! Renunti deja? "
gameforge.locale.monster_chat_304_6 = "Durerea vine foarte repede."
gameforge.locale.monster_chat_305_1 = "Oh doamne! Sabia arata grozav!"
gameforge.locale.monster_chat_305_2 = "Mareste-ti incapatanarea, puterea ta!"
gameforge.locale.monster_chat_305_3 = "Crezi ca poti deveni asa puternic ca mine?"
gameforge.locale.monster_chat_305_4 = "Bine. De ce sa nu jucam serios atunci?"
gameforge.locale.monster_chat_305_5 = "Vai de mine! Eu sunt doar o femeie slaba!"
gameforge.locale.monster_chat_305_6 = "Pari obosit. De ce nu renunti? Ho, ho, ho!"
gameforge.locale.monster_chat_306_1 = "Oare ce va face Serangyi astazi?"
gameforge.locale.monster_chat_306_2 = "Ma plictisesc. Joaca-te cu mine!"
gameforge.locale.monster_chat_306_3 = "Eu il prefer pe tipul Sura."
gameforge.locale.monster_chat_306_4 = "He! Cum ar fi o lupta?"
gameforge.locale.monster_chat_306_5 = "Nu ma subestima doar pentru ca sunt femeie!"
gameforge.locale.monster_chat_306_6 = "Durerea dureaza doar un moment."
gameforge.locale.monster_chat_307_1 = "Vreau cu orice pret sa scap de aici."
gameforge.locale.monster_chat_307_2 = "Cat de sigure sunt coifurile membrilor? Bine?"
gameforge.locale.monster_chat_307_3 = "Voi fetelor, taceti din gura!"
gameforge.locale.monster_chat_307_4 = "Lasa-ma sa te lichidez dintr-o lovitura."
gameforge.locale.monster_chat_307_5 = "Nici sa nu te gandesti sa fugi."
gameforge.locale.monster_chat_307_6 = "Deci, de ce nu incepem?"
gameforge.locale.monster_chat_394_1 = "Nu uita: Toti barbatii sunt inamici."
gameforge.locale.monster_chat_394_2 = "Daca o persoana are o fata draguta, putem fi mai blanzi."
gameforge.locale.monster_chat_394_3 = "Ce, vrei sa lupti?"
gameforge.locale.monster_chat_394_4 = "Nu va dura mult, dar va durea."
gameforge.locale.monster_chat_394_5 = "Ah, pare ca ai cu adevarat dureri."
gameforge.locale.monster_chat_394_6 = "Adio, mergeti in iad!"
gameforge.locale.monster_chat_401_1 = "si, ce fac ei in armata alba?"
gameforge.locale.monster_chat_401_2 = "Unde mergi?"
gameforge.locale.monster_chat_401_3 = "Hei! Vrei sa lupti?"
gameforge.locale.monster_chat_401_4 = "Ha, ha, ha! Eviti loviturile mele."
gameforge.locale.monster_chat_401_5 = "Crezi ca poti sa scapi?"
gameforge.locale.monster_chat_401_6 = "Vine lovitura mea!"
gameforge.locale.monster_chat_402_1 = "Doamne, sunt atat de obosit!"
gameforge.locale.monster_chat_402_2 = "Hei! Frumoasa armura. Te-a costat mult? "
gameforge.locale.monster_chat_402_3 = "Dar este vreme buna."
gameforge.locale.monster_chat_402_4 = "Oh! Se pare ca te-ai antrenat mult. Grozav!"
gameforge.locale.monster_chat_402_5 = "Cum ti se par capacitatile mele? Bune, nu? Ha, ha, ha!"
gameforge.locale.monster_chat_402_6 = "Ce se intampla? Normal! E viteza asta nebuna!"
gameforge.locale.monster_chat_403_1 = "De ce nu merg oare la vanatoare?"
gameforge.locale.monster_chat_403_2 = "Astazi nu ne ataca nimeni satul."
gameforge.locale.monster_chat_403_3 = "Lupta, daca vrei sa lupti! Bastardule!"
gameforge.locale.monster_chat_403_4 = "Ha, ha, ha! Esti totusi doar un amator!"
gameforge.locale.monster_chat_403_5 = "Sa te nimeresc intai in picior sau in brat?"
gameforge.locale.monster_chat_403_6 = "Uite sageata mea!"
gameforge.locale.monster_chat_404_1 = "In curand trebuie sa merg in sat sa ma antrenez."
gameforge.locale.monster_chat_404_2 = "Ma simt intepenit."
gameforge.locale.monster_chat_404_3 = "Ce avem astazi de mancare?"
gameforge.locale.monster_chat_404_4 = "Ma uit, asa ca striga cat poftesti."
gameforge.locale.monster_chat_404_5 = "Prea mult asteptat? Sa vanturam odata sabia!"
gameforge.locale.monster_chat_404_6 = "Sabia suna asa provocator! "
gameforge.locale.monster_chat_405_1 = "Sabia aceasta e pregatita sa inceapa!"
gameforge.locale.monster_chat_405_2 = "Sa vedem pe cine va ucide sabia primul!"
gameforge.locale.monster_chat_405_3 = "Hei! La ce te uiti?"
gameforge.locale.monster_chat_405_4 = "Intai burta!"
gameforge.locale.monster_chat_405_5 = "Teaca nu este standard pentru un luptator cu sabia?"
gameforge.locale.monster_chat_405_6 = "Imi place sa am pe cineva ca tine in loc de teaca."
gameforge.locale.monster_chat_406_1 = "Flamanzesc!"
gameforge.locale.monster_chat_406_2 = "Avem ceva de mancare?"
gameforge.locale.monster_chat_406_3 = "De ce oare mi-e inca foame, dupa ce am mancat atata?"
gameforge.locale.monster_chat_406_4 = "Waaah, mori!"
gameforge.locale.monster_chat_406_5 = "Stomacul meu va decide aceasta lupta!"
gameforge.locale.monster_chat_406_6 = "Este alegerea intre cer si iad!"
gameforge.locale.monster_chat_491_1 = "Nu mai suport! Ma furnica in tot corpul."
gameforge.locale.monster_chat_491_2 = "Oh da. Vad acolo cativa porci."
gameforge.locale.monster_chat_491_3 = "La pamant?"
gameforge.locale.monster_chat_491_4 = "Te lichidez dintr-o lovitura."
gameforge.locale.monster_chat_491_5 = "Aceasta va fi o amintire memorabila!"
gameforge.locale.monster_chat_491_6 = "Mori!"
gameforge.locale.monster_chat_492_1 = "Corpul meu este slabit."
gameforge.locale.monster_chat_492_2 = "Oare de ce nu mai exersez?"
gameforge.locale.monster_chat_492_3 = "Frica? Hm?"
gameforge.locale.monster_chat_492_4 = "Ah, doar o noua victima a armei mele vajaitoare!"
gameforge.locale.monster_chat_492_5 = "Poti vedea stilul meu de atac? Nu? Pierderea ta!"
gameforge.locale.monster_chat_492_6 = "Chiar si o taietura mica dauneaza sanatatii, stiai?"
gameforge.locale.monster_chat_493_1 = "La naiba, e asa de incomod sa ai asa o mana."
gameforge.locale.monster_chat_493_2 = "La ce te uiti? Nu ai mai vazut pe cineva cu asa o mana?"
gameforge.locale.monster_chat_493_3 = "Voi chiar ma calcati pe nervi."
gameforge.locale.monster_chat_493_4 = "Sa te bat mar? Daca nu vrei asta, atunci pleaca acum, bine?"
gameforge.locale.monster_chat_493_5 = "Acum va arat lovitura mea mare!"
gameforge.locale.monster_chat_493_6 = "stiu ca te holbezi la mana mea!"
gameforge.locale.monster_chat_494_1 = "Hei, tu acolo! Daca vrei sa lupti, vino incoace!"
gameforge.locale.monster_chat_494_2 = "Ma cred destul de interesant."
gameforge.locale.monster_chat_494_3 = "De ce nu intreprindem ceva?"
gameforge.locale.monster_chat_494_4 = "Nu, sa iesim nu avem inca voie. Lupta!"
gameforge.locale.monster_chat_494_5 = "Bine, uite!"
gameforge.locale.monster_chat_494_6 = "Esti aproape mort."
gameforge.locale.monster_chat_501_1 = "Joaca-te cu mine! Ramai aici!"
gameforge.locale.monster_chat_501_2 = "As putea sculpta ceva frumos din oasele tale..."
gameforge.locale.monster_chat_501_3 = "Nu am exersat inca."
gameforge.locale.monster_chat_501_4 = "Oh, ce armura frumoasa! Pe aia o vreau."
gameforge.locale.monster_chat_501_5 = "De acum iti va fi greu sa imi ignori atacul!"
gameforge.locale.monster_chat_501_6 = "Ha, ha, ha! Surprins, nu?"
gameforge.locale.monster_chat_502_1 = "Frumusetea nu e totul."
gameforge.locale.monster_chat_502_2 = "Nu te holba, lupta!"
gameforge.locale.monster_chat_502_3 = "Nu imi place cum te holbezi la mine. Vino incoace!"
gameforge.locale.monster_chat_502_4 = "Este o onoare sa mori din sabia mea!!"
gameforge.locale.monster_chat_502_5 = {}
gameforge.locale.monster_chat_502_6 = "Excelent!"
gameforge.locale.monster_chat_503_1 = "Hei! Nu atinge!"
gameforge.locale.monster_chat_503_2 = "Se pare ca mi s-a rupt coarda arcului."
gameforge.locale.monster_chat_503_3 = "La naiba! Au venit idiotii astia."
gameforge.locale.monster_chat_503_4 = "Nu-i rau. Dar totusi ai sa mori!"
gameforge.locale.monster_chat_503_5 = "Esti de acord cu asta? E divin!"
gameforge.locale.monster_chat_503_6 = "Uite sageata!"
gameforge.locale.monster_chat_504_1 = "Veniti toti incoace!"
gameforge.locale.monster_chat_504_2 = "La, lala, fredonez un cantec..."
gameforge.locale.monster_chat_504_3 = "Ce vreme frumoasa."
gameforge.locale.monster_chat_504_4 = {}
gameforge.locale.monster_chat_504_5 = "Bine! Iti apreciez dorinta de lupta!"
gameforge.locale.monster_chat_504_6 = "Veniti aici! Toata lumea la mine!"
gameforge.locale.monster_chat_601_1 = "Sunt asa flamand!"
gameforge.locale.monster_chat_601_2 = "As vrea sa mananc fructe."
gameforge.locale.monster_chat_601_3 = "Ei bine, saramura facem. La atac!"
gameforge.locale.monster_chat_601_4 = "Nu fugi! Stai pe loc!"
gameforge.locale.monster_chat_601_5 = "Am castigat! Este evident!"
gameforge.locale.monster_chat_601_6 = "Vezi? Ai ratat!"
gameforge.locale.monster_chat_601_7 = "Trebuie sa ai grija, haha!"
gameforge.locale.monster_chat_602_1 = "Unde sunt?"
gameforge.locale.monster_chat_602_2 = "Frumoasa vreme. "
gameforge.locale.monster_chat_602_3 = "Vii sa ma vanezi? Ridicol!"
gameforge.locale.monster_chat_602_4 = "Tu esti prada mea. "
gameforge.locale.monster_chat_602_5 = "Ce teroare, hm? Bine, lasa-ma sa te lichidez!"
gameforge.locale.monster_chat_602_6 = "Uite cat de tare sunt: pot invarti acest ciomag gigant!"
gameforge.locale.monster_chat_603_1 = "Plictisitor!"
gameforge.locale.monster_chat_603_2 = "Ma furnica in tot corpul."
gameforge.locale.monster_chat_603_3 = "Cine reuseste primul."
gameforge.locale.monster_chat_603_4 = "Tu m-ai nimerit primul? Am sa te alung."
gameforge.locale.monster_chat_603_5 = "Odata ce ai intrat, nu mai poti iesi. Fuga este de prisos acum!"
gameforge.locale.monster_chat_603_6 = "Aceasta va fi ultima lovitura. Te lichidez!"
gameforge.locale.monster_chat_604_1 = "Am facut o noua otrava . Doresti sa gusti?"
gameforge.locale.monster_chat_604_2 = "Vreau sa vad cum merge! Poftim, ia-o!"
gameforge.locale.monster_chat_604_3 = "Hei, tu de acolo! Vino aici, vrei sa te fac cobaiul meu?"
gameforge.locale.monster_chat_604_4 = "Te otravesc! "
gameforge.locale.monster_chat_604_5 = "Oh, ce bine! Un soarece de laborator!"
gameforge.locale.monster_chat_604_6 = "Nu e distractiv daca renunti asa repede! Haide, apara-te!"
gameforge.locale.monster_chat_631_1 = "Asteapta un moment. Caut ceva sa vanez."
gameforge.locale.monster_chat_631_2 = "Nu ajuta cu nimic daca te misti atat de incet Misca!"
gameforge.locale.monster_chat_631_3 = "Mi-e asa de foame!"
gameforge.locale.monster_chat_631_4 = "Este nemaipomenit ca am gasit ce sa vanez!"
gameforge.locale.monster_chat_631_5 = "Toti speram sa supravietuim! Tu nu vei supravietui!"
gameforge.locale.monster_chat_631_6 = "Acum vei afla ce inseamna cu adevarat durerea!"
gameforge.locale.monster_chat_632_1 = "Plictisitoooor!"
gameforge.locale.monster_chat_632_2 = "Exista vreo momeala buna?"
gameforge.locale.monster_chat_632_3 = "Vino si lupta daca vrei!"
gameforge.locale.monster_chat_632_4 = "Te crezi razboinic? Atunci arata ce poti!"
gameforge.locale.monster_chat_632_5 = {}
gameforge.locale.monster_chat_632_6 = "Fugi in sat daca vrei sa ramai in viata!"
gameforge.locale.monster_chat_633_1 = "Doare sa flamanzesti."
gameforge.locale.monster_chat_633_2 = "Oh, vin spre noi."
gameforge.locale.monster_chat_633_3 = "Cum ar fi cu un duel?"
gameforge.locale.monster_chat_633_4 = "Nu ma compara cu acesti bastarzi!"
gameforge.locale.monster_chat_633_5 = "Acum vine lovitura mea de tunet!"
gameforge.locale.monster_chat_633_6 = "Cei care fug nu sunt razboinici adevarati!"
gameforge.locale.monster_chat_634_1 = "De data aceasta otrava trebuie sa fie cu adevarat buna!"
gameforge.locale.monster_chat_634_2 = "Cand ti-e foame, liliecii sunt o delicatesa!"
gameforge.locale.monster_chat_634_3 = "Cum ar fi cu o lupta?"
gameforge.locale.monster_chat_634_4 = "Dupa ce otrava mea a ajuns in corp, nu mai exista vindecare."
gameforge.locale.monster_chat_634_5 = "Magia mea va arde totul imprejur, hehehe!"
gameforge.locale.monster_chat_634_6 = "Arzi! Ha, ha!"
gameforge.locale.monster_chat_635_1 = "Nu uita niciodata sa-ti ascuti sabia. Urmatoarea lupta e mai devreme decat crezi."
gameforge.locale.monster_chat_635_2 = "Lesin?"
gameforge.locale.monster_chat_635_3 = "Mori si reinvie in alta viata!"
gameforge.locale.monster_chat_635_4 = "Pe mine sa ma infrangi? Serios?"
gameforge.locale.monster_chat_635_5 = "Lupta ca un razboinic!"
gameforge.locale.monster_chat_635_6 = "Haide, inca putin de tot."
gameforge.locale.monster_chat_636_1 = "Ha, caut o lupta frumoasa."
gameforge.locale.monster_chat_636_2 = "Lupta e distractiva. (Ras)"
gameforge.locale.monster_chat_636_3 = "As dori sa lupt in continuu!"
gameforge.locale.monster_chat_636_4 = "Foarte bine! Incearca din nou!"
gameforge.locale.monster_chat_636_5 = "Uita-te cum imi vantur sabia!"
gameforge.locale.monster_chat_636_6 = "O sa iti para rau ca m-ai nimerit!"
gameforge.locale.monster_chat_636_7 = "Asa de slab! Cat de ridicol!"
gameforge.locale.monster_chat_637_1 = "Nu vreau sa fiu deranjat."
gameforge.locale.monster_chat_637_2 = "Mergi la culcare. Pentru totdeauna!"
gameforge.locale.monster_chat_637_3 = "Nu ai adormit inca?"
gameforge.locale.monster_chat_637_4 = "Sunt atat de multe de facut! Sa terminam cu ele!"
gameforge.locale.monster_chat_637_5 = "Nu te mira de puterea mea!"
gameforge.locale.monster_chat_637_6 = "Te pulverizez!"
gameforge.locale.monster_chat_691_1 = "Grozav, oponentii puternici rezista mai mult! Apara-te!"
gameforge.locale.monster_chat_691_2 = "Ce? Cum arat?"
gameforge.locale.monster_chat_691_3 = "Nu ma compara cu acest neputiincios."
gameforge.locale.monster_chat_691_4 = "Nici el nu te place iar eu nici atat!"
gameforge.locale.monster_chat_691_5 = "Sa luptam."
