gameforge.main_quest_lv47._500_sayTitle = "A opta pagina"
gameforge.main_quest_lv47._50_sayTitle = "A cincea pagina:"
gameforge.main_quest_lv47._510_say = "Jurnalul spune despre sacrificarea serpilor[ENTER]Spadasini si serpilor Arcasi pentru ritualul[ENTER]magiei negre. Mergi in desertul Yongbi si ucide[ENTER]serpi Spadasini. Si gaseste si alte informatii."
