gameforge.main_quest_lv92._200_say = "Ce se intampla?"
gameforge.main_quest_lv92._210_say = "Exista si a cincea bucata din Scrisoarea Talhar[ENTER]Rosu. Fara aceasta bucata, restul scrisorii este[ENTER]fara valoare. L-am trimis deja pe Bae-Yong, unul[ENTER]dintre oamenii mei de incredere dar ceva cred ca[ENTER]s-a intamplat.[ENTER]Trebuia sa se intoarca deja de mult timp. Poti[ENTER]merge sa-l cauti?"
gameforge.main_quest_lv92._220_say = "Desigur! Stii unde ar putea fi acum?"
