gameforge.subquest_13._150_say = "Am pierdut ceva cand am fugit de tigri pentru[ENTER]a-mi salva viata: un pachet care continea carti[ENTER]importante, legate intre ele cu niste carpe.[ENTER]Daca nu le recuperez nu voi putea continua[ENTER]cercetarile despre Pietrele Metin. Adu-mi[ENTER]pachetul inapoi si vei primi niste minereu de[ENTER]fier."
gameforge.subquest_13._160_npcChat = "Am gasit pachetul."
gameforge.subquest_13._170_say = "Multumesc![ENTER]Apelez la tine mult prea des pentru sevicii.[ENTER]Poftim niste minereu de fier. Sper sa-si[ENTER]indeplineasca scopul."
gameforge.subquest_13._180_say = "Poftim? Ai nevoie de mai mult minereu de fier?[ENTER]Esti constient ca nu creste in copaci! Iti voi da[ENTER]mai mult dar inainte trebuie sa-mi faci o[ENTER]favoare. Tigrii mi-au furat Pachetul. A trebuit[ENTER]sa fug pentru a-mi salva viata si erau documente[ENTER]foarte importante in el. Daca nu-l pot recupera[ENTER]nu voi putea continua cercetarea despre Pietrele[ENTER]Metin. Te rog recupereaza-mi pachetul."
gameforge.subquest_13._190_say = "Dar ti-am dat deja. Ai uitat?"
gameforge.subquest_13._200_sayTitle = "Pachet gasit"
gameforge.subquest_13._20_npcChat = "Material pentru arme"
gameforge.subquest_13._210_say = "Ai gasit bocceaua lui Uriel. Du-te inapoi la el."
gameforge.subquest_13._220_npcChat = "Da-mi in."
gameforge.subquest_13._230_say = "Ai nevoie de pinza? Am dat-o lui Ah-Yu. Asteapta![ENTER]Daca tot o vezi ii poti da si aceasta scrisoare.[ENTER]Suntem prieteni buni dar din cauza copilului ei[ENTER]nu ne-am mai vazut demult. Ii voi spune sa-ti dea[ENTER]si pinza ..."
gameforge.subquest_13._240_say = "Ariyoung:[ENTER]Cu siguranta ca ai primit in de la Ah-Yu, nu-i[ENTER]asa?"
gameforge.subquest_13._250_npcChat = "Scrisoarea lui Ariyoung"
gameforge.subquest_13._260_say = "O scrisoare de la Ariyoung? Este placut sa auzi[ENTER]vesti de la ea dupa atata timp. Obisnuiam sa ne[ENTER]intalnim des sa vorbim dar in ultimul timp a[ENTER]devenit cam greu acest lucru. Asteapta putin.[ENTER]Scrie in scrisoare ca ai nevoie de Panza!?[ENTER]Ariyoung mi-a dat asa ceva si iti pot da si tie.[ENTER]Multumesc ca ai adus scrisoarea.[ENTER]Ii voi trimite raspuns imediat..."
gameforge.subquest_13._270_qSetTitle = "Sa mergem la Negustorul de arme"
gameforge.subquest_13._280_targetVid = "Mergi la negustorul de arme."
gameforge.subquest_13._290_sayTitle = "Preda materialele"
gameforge.subquest_13._300_sayReward = "Am marfa. Hai la negustorul de arme."
gameforge.subquest_13._30_say = "Hei, trebuie sa-mi faci un serviciu. Incerc sa[ENTER]confectionez arme noi. Nu e usor sa gasesc[ENTER]materialele adecvate pentru aceasta. Am multe[ENTER]comenzi si nu pot sa plec in cautarea[ENTER]materialelor. Ai putea sa le procuri tu in locul[ENTER]meu?"
gameforge.subquest_13._310_say = "Bravo. Esti foarte rapid si capabil. Lasa-ma acum[ENTER]sa ma ocup de arme.[ENTER]*cioc, cioc *[ENTER]Iata-le. Acestea sunt noile tipuri de arme.[ENTER]Am facut tot ce am putut. Sper ca-ti place arma.[ENTER]Sa te bucuri de ea!"
