gameforge.subquest_13._340_sayReward = "Ai primit Sabie lata +3."
gameforge.subquest_13._350_sayReward = "Ai primit Pumnal Foarfece +3."
gameforge.subquest_13._360_sayReward = "Ai primit Evantai Paun +3."
gameforge.subquest_13._40_say = "Bineinteles ca te voi rasplati pentru asta. Cand[ENTER]armele vor fi gata iti voi da una din ele.[ENTER]Am nevoie de urmatoarele materiale: minereu de[ENTER]fier si in.[ENTER]Minereu de fier gasesti la Uriel si in la[ENTER]Ariyoung. Da-mi de stire cand ai gasit toate[ENTER]materialele."
gameforge.subquest_13._50_say = "Chiar nu vrei sa indeplinesti sarcina?"
