gameforge.subquest_13._70_say = "Daca nu vrei, nu se poate face nimic.[ENTER]Pe curand."
gameforge.subquest_13._80_say = "Foarte bine![ENTER]De indata ce armele vor fi gata, vei primi una de[ENTER]la mine. Mult noroc."
gameforge.subquest_13._90_sendLetter = "Cauta materiale"
