gameforge.ghost_story = {}
gameforge.ghost_story._010_sayTitle = "Fantoma unui Sura: "
gameforge.ghost_story._020_npcChat = "Esti o fantoma? "
gameforge.ghost_story._030_say = "No, sunt Regina din Sheeba... Normal ca sunt o[ENTER]fantoma! Te-a scapat cineva in cap cand ai fost[ENTER]copil? "
gameforge.ghost_story._040_npcChat = "Ajutor! O fantoma! "
gameforge.ghost_story._050_say = "Un razboinic neinfricat! Boo!! "
gameforge.ghost_story._060_npcChat = "Cine esti tu? "
gameforge.ghost_story._070_say = "Numele meu este Akuma. Am fost o data un mare razboinic,[ENTER]unii spun ca am fost cel mai bun la vremea mea. Si eu chiar[ENTER]m-am simtit asa! Dar uite-ma acuma. Uita-te[ENTER]cea mai ramas din mine dupa ce-am gasit Grota Exilului...[ENTER]totul a disparut. "
gameforge.ghost_story._090_say = "Dar stiu ce s-a intamplat atunci. Am cercetat[ENTER]pentru mult timp, incercand sa-mi dau seama care-i motivul[ENTER]in spatele acestei Grote. Si[ENTER]de cand cu moartea mea am mai mult timp sa[ENTER]ma gandesc. Doar intreaba-ma ce doresti! "
gameforge.ghost_story._100_npcChat = "Ce fel de portal este acesta? "
gameforge.ghost_story._110_say = "Acest portal te duce in camera[ENTER]dragonului, la Zeul decazut Beran-Setaou. Poti intra[ENTER]doar cu o cheie magica, care doar[ENTER]trupele lui Beran-Setaou le dropeaza... "
gameforge.ghost_story._120_npcChat = "Cei trei zei "
gameforge.ghost_story._130_say = "O data ca niciodata, trei dintre fratii zei[ENTER]au facut viata.[ENTER]Bahar-Taraji, Regina Creatiei a fost[ENTER]cea mai tanara. Misiunea ei era sa creeze noi[ENTER]fapturi - oriunde ar fii mers, florile ar fii inflorit si[ENTER]si ar lua viata. "
gameforge.ghost_story._140_say = "Cel mai batran frate a fost Beran-Setaou. El[ENTER]era responsabil cu sfarsitul vieti isi cu[ENTER]dizolvarea. De fiecare data cand un ciclu al vietiiENTER]se sfarsea, corpul si sufletul se separa[ENTER]iar sufletul duce o viata dupa.[ENTER]Iar astfel ciclul intre a deveni si a te duce[ENTER]departe - piatra de temelie a vietii - era securizata. "
gameforge.ghost_story._150_say = "Fratele mijlociu era Baljit-Elvedin. Ca Zeu al[ENTER]Conservarii, el avea grija de[ENTER]cei doi frati si a deschis ciclul etern al vietii pentru[ENTER]creaturile de pe pamant. El a fost,[ENTER]responsabil cu venirea si plecarea[ENTER]anotimpurilor. "
gameforge.ghost_story._160_npcChat = "Cearta "
gameforge.ghost_story._170_say = "In timpuri grele, oamenii i-au rugat pe cei trei Zei[ENTER]sa-i ajute. Desii din pacate ei se temeau[ENTER]si nu l-au luat in considerare pe Beran-Setaou, ei au fost[ENTER]orbi si nu au inteles ca doar[ENTER]lumina si intuneric exista, de asemenea acesta[ENTER]a avut si o parte rea a creatiei. "
gameforge.ghost_story._180_say = "Pentru mult timp Zeul a ignorat, dar peste ani[ENTER]gelozia a crescut in el, care incetisor[ENTER]s-a transformat in ura. Pentru inceput acesta si-a[ENTER]pus furia asupra oamenilor, alungandu-i in razboi[ENTER]si imbolnavindu-i. Cu toate acestea, asta a dus doar la[ENTER]o viitoare respingere, acestia s-au[ENTER]rugat la Bahar-Taraji, zeita celor noi creati[ENTER]in viata. Acesta a devenit un cerc vicios[ENTER]care in final a dus la ruina. "
gameforge.ghost_story._190_say = "Iar dupa ce blestemul zeului a devenit asa de intens[ENTER], incat nu l-a mai putut controla,[ENTER]ceva de necrezut s-a inamplat: A ucis-o pe sora[ENTER]sa, inima dulce a natiunii. Aceste fapte au fost[ENTER]toate provocate, din cauza acelei zi in care si-a[ENTER]tratat fratele cu dragoste si incredere[ENTER], ca in fiecare zi. Iar el, s-a uitat in acesti ochi[ENTER]inocenti si i-a luat viata. "
gameforge.ghost_story._200_npcChat = "Urmarile "
gameforge.ghost_story._210_say = "Moartea lui Bahar-Taraji's a avut teribile consecinte[ENTER]pentru lumea zeilor si la fel pentru a cea a[ENTER]oamenilor: O lunga nemiloasa iarna a venit, aceasta a ucis[ENTER]creaturi din mare si de pe mal. Pamantul a inghetat,[ENTER]pietrele au inceput sa se sparga iar chiar[ENTER]si ce-i mai vechi si tari copaci s-au ofilit. "
gameforge.ghost_story._220_say = "Oameni tineri si animale au murit, intr-un timp[ENTER]cand vremea s-a incalzit, rata natalitatii a devenit mult mai scazuta[ENTER]decat inainte. Tinutul pricajit nu a dat destul randament[ENTER]incat sa tina populatia adulta in viata - [ENTER]nu exista destula mancare pentru o viata noua. Speranta a murit[ENTER]deodata cu viata iar amandoua au avut nevoie de[ENTER]mult timp sa revina. "
gameforge.ghost_story._230_say = "Chiar si Zeii au suferit, insa, timpul trecea[ENTER]pentru ei mult mai incet fata de oameni.[ENTER]Beran-Setaou s-a retras si si-a cautat singuratatea, odata[ENTER]ce a vazut ceea ce a creat.[ENTER]Regretul devastator si satisfactia se luptau[ENTER]inauntrul lui - pana cand mai tarziu a castigat mental[ENTER]aceasta lupta. Inima lui s-a transformat in gheata, Zeul[ENTER]Dezintegrarii a devenit un Zeu Rece plin de,[ENTER]ura si teama. "
gameforge.ghost_story._240_npcChat = "Batalia "
gameforge.ghost_story._250_say = "Baljit-Elvedin, Zeul Conservarii, era intristat[ENTER]pentru sora lui de mult timp. Si in timp ce[ENTER]ghearele durerii isi lasau amprenta[ENTER]pe inima lui, a pornit sa-l caute pe fratele lui.[ENTER]Intelepciunea inca continua sa-i domnie[ENTER]calea in viata, iar aceasta era de recunoscut,cu toate ca[ENTER]era indurerat, acea viata nu putea sa continue in[ENTER]dezechilibru. "
gameforge.ghost_story._260_say = "A urmat traseul fratelui sau pentru[ENTER]multi ani, intr-un final l-a gasit in Grota de Gheata,[ENTER]ura si solitudine, indepratat de orice[ENTER]semn de viata. Baljit-Elvedin a facut ceea ce a[ENTER]trebui sa faca, chiar daca acest lucru i-a adus inca o durere[ENTER]pe inima: L-a provocat pe fratele sau la un duel.[ENTER]Beran-Setaou a deraiat atat de mult de la calea[ENTER]bunatatii incat nu a ezitat pentru nicio[ENTER]secunda... "
gameforge.ghost_story._270_say = "Aparent, lupta a fost nemilioasa si fara nicio[ENTER]intelegere. Baljit-Elvedin a castigat lupta.[ENTER]Si pentru principiile sale morale, nu l-a ucis pe[ENTER]fratele sau, insa i-a luat toate puterile si l-a inchis[ENTER]in Grota. Acest Beran-Setaou a ramas[ENTER]acolo cu creaturile care s-au format acolo[ENTER]in ultimele secole... "
gameforge.ghost_story._280_npcChat = "Am auzit destul. "
gameforge.ghost_story._290_say = "Sper ca soarta ta sa fie diferita de a mea. Mult noroc! "
gameforge.ghost_story._300_sayTitle = "Fantoma unui razboinic: "
gameforge.ghost_story._310_npcChat = "De ce esti transparent? "
gameforge.ghost_story._320_say = "Ce crezi?! Sunt o fantoma si sunt[ENTER]incatusat aici de mii de ani... "
gameforge.ghost_story._330_say = "Ajutor, o fantoma! "
gameforge.ghost_story._340_say = "Da, o fantoma. Fugi departe repede, altfel am sa[ENTER]te mananc! "
gameforge.ghost_story._350_npcChat = "Cine esti? "
gameforge.ghost_story._360_say = "Am fost un mercenar, un simplu om. Parintii mei[ENTER]erau fermieri. Desii, nu am vrut sa muncesc in[ENTER]acelasi loc toata viata mea,[ENTER]m-am dus in armata. Era greu si[ENTER]sangeros, dar n-a fost totul rau. Dar dupa aceea[ENTER]m-am plictisit sa fiu condus de altii pentru[ENTER]niste nobili buni de nimic si am parasit. Iar apoi soarta[ENTER]m-a adus in acest loc sinistru. "
gameforge.ghost_story._370_npcChat = "Stii povestea grotei? "
gameforge.ghost_story._380_say = "Nu, nu o stiu, ma gandesc a am fost doar nenorocos.[ENTER]Dupa ce am dezertat, m-am gandit sa fug in munti[ENTER]dar m-am impiedicat de aceasta grota. Imi parea cam[ENTER]straniu chiar la inceput, dar era[ENTER]convenabil si parea foarte parasita. Cateva[ENTER]zile mai tarziu am devenit martor la o vitcima[ENTER]dintr-o lupta de necrezut. Daca iti place iti pot spune desprea[ENTER]ea. "
gameforge.ghost_story._390_npcChat = "Inceputul bataliei "
gameforge.ghost_story._400_say = "Intr-o zi - colectam radacini[ENTER]din munte - un dragon argintiu a aparut[ENTER]din rai. Am crezut ca a venit dupa mine, dar nici[ENTER]macar nu m-a vazut - era complet focusat[ENTER]pe intrarea grotei. Fara sa[ENTER]incetineasca, s-a napustit in vale si s-a lansat in[ENTER]grota. "
gameforge.ghost_story._410_say = "S-a facut liniste dintr-o data si nu am stiut[ENTER]ce sa mai fac. Tot echipamentul meu era[ENTER]in grota, dar desigur nu voiam sa-i stau in calea[ENTER]dragonului! Pamantul a inceput sa se cutremure si un[ENTER]raget s-a auzit din adancul grotei -[ENTER]dar spre oroarea mea nu a fost in singur raget - ci[ENTER]doua! La scurt timp din grota au iesit doi[ENTER]dragoni - unul argintiu urmat de unul[ENTER]albastru. "
gameforge.ghost_story._420_say = "O batalie de nedescris s-a desfasurat[ENTER]intre cei doi dragoni. Au inceput sa-si sfasie bucati unul din celalalt,[ENTER]acestia s-au incaierat unul asupra altuia[ENTER], cu coltii asupra gatului... Parca ploua[ENTER]cu sange... Dar niciunul[ENTER]nu si-a pierdut forta pentru o secunda. "
gameforge.ghost_story._430_npcChat = "Sfarsitul luptei "
gameforge.ghost_story._440_say = "Ambii dragoni s-au rostolit peste munte ca o[ENTER]furtuna - cauzand aluncari de teren si haos[ENTER]imprejur. Fulgere au ars gura Dragonului[ENTER]Albastru, grindina a perforat aripile Dragonului[ENTER]Argintiu. Crusta groasa de gheata acoperea[ENTER]tacest pamant de ani, topita sub aceasta lupta[ENTER]si lasand noroi in urma. Eram ascuns dupa[ENTER]o roca, pietrificat insa incapabil sa ignor[ENTER]privelistea din fata mea. "
gameforge.ghost_story._450_say = "Au fost nevoie de ore pana cand puterea lor sa se[ENTER]deterioreze. Iar mai apoi am vazut sansa,[ENTER]sa o rup la fuga si sa ajung in vale ca sa[ENTER]imi recuperez echipamentul din grota. Iar acestea au sigilat[ENTER]a mea soarta. Eram deja in drumul spre iesire cand cei doi[ENTER]dragoni se luptau in afara muntilor, cel albastru[ENTER]in fata celui argintiu. Acesta a suferit teribile[ENTER]pagube. "
gameforge.ghost_story._460_say = "Chiar inainte sa dispara in grota, dragonul[ENTER]argintiu l-a prins iar ceva foarte[ENTER]straniu s-a intamplat: Cerul a inceput sa tremure, a devenit[ENTER]extrem de intunecat iar un vortex de energie[ENTER]a fost creat in care dragonul albastru a fost[ENTER]cuprins. A inceput sa se auda rageturi de durere...[ENTER]a inceput sa scada, ragetul sau slabea[ENTER]iar balantele lui nu mai aveau aceeasi sclipitre[ENTER], erau tocite si fara culoare... "
gameforge.ghost_story._470_say = "In sfarsit dragonul argintiu l-a lasat pe celalalt[ENTER]dragon in grota. Cu o ultima picatura de[ENTER]putere, pierzatorul s-a tarat sub[ENTER]munti. In final, dragonul argintiu a inceput sa-si[ENTER]dezlantuiasca furtuna magica, care a sigilat[ENTER]grota cu o bariera facuta din energie si a[ENTER]muntii sa intre in colaps. Dragonul intuncat si eu eram[ENTER]prinsi acolo. Condamnati la om mizerabila[ENTER]moarte datorita foametei... "
gameforge.ghost_story._480_npcChat = "Am auzit destul. "
gameforge.ghost_story._490_say = "Multumesc ca ai ascultat. Ultimii mii de ani[ENTER]au fost foarte singuratici.. "

